export type ParsedLiquidateInstruction<
  TProgram extends string = typeof OMNIPAIR_PROGRAM_ADDRESS,
  TAccountMetas extends readonly AccountMeta[] = readonly AccountMeta[],
> = {
  programAddress: Address<TProgram>;
  accounts: {
    pair: TAccountMetas[0];
    userPosition: TAccountMetas[1];
    rateModel: TAccountMetas[2];
    futarchyAuthority: TAccountMetas[3];
    collateralVault: TAccountMetas[4];
    callerTokenAccount: TAccountMetas[5];
    collateralTokenMint: TAccountMetas[6];
    reserveVault: TAccountMetas[7];
    positionOwner: TAccountMetas[8];
    payer: TAccountMetas[9];
    tokenProgram: TAccountMetas[10];
    token2022Program: TAccountMetas[11];
    systemProgram: TAccountMetas[12];
    eventAuthority: TAccountMetas[13];
    program: TAccountMetas[14];
  };
  data: LiquidateInstructionData;
};

export function parseLiquidateInstruction<
  TProgram extends string,
  TAccountMetas extends readonly AccountMeta[],
>(
  instruction: Instruction<TProgram> &
    InstructionWithAccounts<TAccountMetas> &
    InstructionWithData<ReadonlyUint8Array>,
): ParsedLiquidateInstruction<TProgram, TAccountMetas> {
  if (instruction.accounts.length < 15) {
    // TODO: Coded error.
    throw new Error("Not enough accounts");
  }
  let accountIndex = 0;
  const getNextAccount = () => {
    const accountMeta = (instruction.accounts as TAccountMetas)[accountIndex]!;
    accountIndex += 1;
    return accountMeta;
  };
  return {
    programAddress: instruction.programAddress,
    accounts: {
      pair: getNextAccount(),
      userPosition: getNextAccount(),
      rateModel: getNextAccount(),
      futarchyAuthority: getNextAccount(),
      collateralVault: getNextAccount(),
      callerTokenAccount: getNextAccount(),
      collateralTokenMint: getNextAccount(),
      reserveVault: getNextAccount(),
      positionOwner: getNextAccount(),
      payer: getNextAccount(),
      tokenProgram: getNextAccount(),
      token2022Program: getNextAccount(),
      systemProgram: getNextAccount(),
      eventAuthority: getNextAccount(),
      program: getNextAccount(),
    },
    data: getLiquidateInstructionDataDecoder().decode(instruction.data),
  };
}
