    reserveVault: TAccountMetas[4];
    userReserveTokenAccount: TAccountMetas[5];
    reserveTokenMint: TAccountMetas[6];
    user: TAccountMetas[7];
    tokenProgram: TAccountMetas[8];
    token2022Program: TAccountMetas[9];
